graphql.query:
query {
  organization {
    user {
    get(input: {id: "f9d7e63f-ecfc-4a0e-8192-a2a36312d205"}) {
    __typename  
      ... on UserGetSuccess {
      user {
        id
        name
      }
    }
    ... on UserGetFailure {


    }
    }
  } 
}
}

query {
  organization {
    user {
    me {
    __typename  
      ... on UserGetSuccess {
      user {
        id
        name
      }
    }
      ... on UserGetFailure {
        error {
            message
        }
      }
    }
  } 
}
}

query {
  organization {
    user {
      list(
        input: {
          filter: {
            teamId: "f9d7e63f-ecfc-4a0e-8192-a2a36312d205"
          },
          pagination: {
            first: 10
          }
        }
      ) {
        __typename
        ... on UserListSuccess {
          users {
            edges {
              node {
                id
                name
              }
            }
          }
        }
        ... on UserListFailure {
          error {
            message
          }
        }
      }
    }
  }
}
